import React from "react";

const RecipeFilters = ({ filters }) => {
  return (
    <div
      className=" col-auto btn-group-vertical"
      role="group"
      aria-label="Recipe filters"
    >
      <div>Filters</div>
      {filters.map((filter) => (
        <React.Fragment>
          <input
            type="radio"
            className="btn-check bg-white"
            name={filter.name}
            id={filter._id}
          ></input>
          <label
            className="btn"
            for={filter.name}
            active={filter.name === "All"}
          >
            {filter.name}
          </label>
        </React.Fragment>
      ))}
    </div>
  );
};

export default RecipeFilters;
