import React from "react";

const RecipeFilters = ({ filters, selectedFilter, onFilterSelect }) => {
  const isActive = (filter, index) => {
    if (selectedFilter == null) {
      if (index === 0) return true;
      else return false;
    } else {
      if (filter === selectedFilter) return true;
      else return false;
    }
  };
  return (
    <ul className="bgLightGray col-auto list-group p-0 rounded-0">
      <li className="list-group-item border-0 bg-transparent">Filters</li>
      {filters.map((filter, index) => (
        <li
          className={
            (isActive(filter, index) ? "bg-black" : "") +
            "pointerHover list-group-item border-0 bg-transparent"
          }
          key={filter._id}
          onClick={() => onFilterSelect(filter)}
        >
          {filter.name}
        </li>
      ))}
    </ul>
  );
};

export default RecipeFilters;
