import React from "react";

const RecipeFilters = ({ filters, onFilterSelect }) => {
  //   const isActive = (item, index) => {
  //     if (selectedItem == null) {
  //       if (index === 0) return true;
  //       else return false;
  //     } else {
  //       if (item === selectedItem) return true;
  //       else return false;
  //     }
  //   };
  return (
    <ul className="bgLightGray col-auto list-group p-0 rounded-0">
      <li className="list-group-item border-0 bg-transparent">Filters</li>
      {filters.map((filter) => (
        <li
          className="pointerHover list-group-item border-0 bg-transparent"
          key={filter._id}
          onClick={() => onFilterSelect(filter)}
        >
          {filter.name}
        </li>
      ))}
    </ul>
  );
};

export default RecipeFilters;
